var plan = require('./index');

plan.target('single', {
  host: 'jookbox.me',
  username: 'pstadler',
  agent: process.env.SSH_AUTH_SOCK
}, { sudoUser: 'bar' });

plan.target('multi', [
  {
    host: 'jookbox.me',
    username: 'pstadler',
    agent: process.env.SSH_AUTH_SOCK
  },
  {
    host: 'jookbox.me',
    username: 'pstadler',
    agent: process.env.SSH_AUTH_SOCK
  },
]);

plan.local(function(local) {
  console.log(plan.runtime.options);
  local.failsafe();
  local.ls('-al', {exec: { cwd: '/'}});
});

plan.remote(function(remote) {
  remote.silent();
  remote.ls('-al');
  remote.verbose();
  remote.ls('-al');
  remote.exec('ls -al', { exec: { pty: true } });
});

